pragma solidity >=0.5.1;

interface IPFS {
    function add(bytes calldata data) external returns (bytes32);
    function cat(bytes32 ipfsHash) external view returns (bytes memory);
    function remove(bytes32 ipfsHash) external;
}

contract IPFSFileStorage {
    IPFS private ipfs;
    mapping (bytes32 => address) private fileOwners;
    
    constructor(address _ipfsAddress) public {
        ipfs = IPFS(_ipfsAddress);
    }
    
    function addFile(bytes memory data) public returns (bytes32) {
        require(data.length > 0, "Empty file data");
        
        bytes32 hash = ipfs.add(data);
        fileOwners[hash] = msg.sender;
        
        return hash;
    }
    
    function removeFile(bytes32 ipfsHash) public {
        require(fileOwners[ipfsHash] == msg.sender, "You don't own this file");
        
        ipfs.remove(ipfsHash);
        delete fileOwners[ipfsHash];
    }
    
    function getFile(bytes32 ipfsHash) public view returns (bytes memory) {
        return ipfs.cat(ipfsHash);
    }
}
